import {
  ADD_ITEM,
  TOGGLE_CART,
  TOGGLE_ERROR
  // UPDATE_ACCOUNT
} from '../actions/types';

const initialState = {
  // token: localStorage.getItem('token'),
  // isAuthenticated: null,
  // loading: true,
  // user: null,
  cartItems: [],
  hidden: true
}

export default function(state = initialState, action) {
  const { type, payload } = action;

  switch(type) {
      case ADD_ITEM:
        return {
          ...state,
          cartItems: [...state.cartItems, payload]
        }
      case TOGGLE_CART:
        return {
          // ...state,
          hidden: !state.hidden
        }
      case TOGGLE_ERROR:
        return {
          ...state
        }
      default:
        return state
  }
}
